  hostype<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 医院类型标识</span>
  provinceCode<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 省编码</span>
  cityCode<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 市编码</span>
  districtCode<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 区编码</span>
  logoData<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 医院logo</span>
  intro<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 医院介绍</span>
  route<span class="token operator">:</span> <span class="token builtin">string</span><span class="token punctuation">;</span> <span class="token comment">// 交通方式</span>
  status<span class="token operator">:</span> Status<span class="token punctuation">;</span> <span class="token comment">// 状态：0：未上线 1：已上线</span>
<span class="token punctuation">}</span>
<span class="token comment">// 单个医院类型（之前）</span>
<span class="token keyword">export</span> <span class="token keyword">interface</span> <span class="token class-name">HospitalItemType</span> <span class="token keyword">extends</span> <span class="token class-name">HospitalType</span> <span class="token punctuation">{</span>
  bookingRule<span class="token operator">:</span> BookingRuleType<span class="token punctuation">;</span> <span class="token comment">// 预约规则信息</span>
